mapper.select:
select user_id, activity_id, day, day_count, day_count_surplus
from raffle_activity_account_day
where user_id = #{userId} and activity_id = #{activityId} and day = #{day}

select day_count - day_count_surplus
from raffle_activity_account_day
where user_id=#{userId} and activity_id = #{activityId} and day = #{day}
